import java.util.Scanner;

public class simpleinterest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner jogar = new Scanner(System.in);
		int p=jogar.nextInt();
		int t=jogar.nextInt();
		int r=jogar.nextInt();
		int s=(p*t*r)/100 ;
		
		System.out.print(s);
		
		
		

	}

}
